import XCTest
@testable import z80

class Z80FuseTests: XCTestCase {
    var z80: Z80 = .init()

    override func setUp() {
        // reinitialize memory and processor start state
        z80.memory.reset()
        z80.reset()
    }

    func poke(_ address: UInt16, _ value: UInt8) { z80.memory.writeByte(address, value) }
    func peek(_ address: UInt16) -> UInt8 { z80.memory.readByte(address) }

    func loadRegisters(af: UInt16 = 0, bc: UInt16 = 0, de: UInt16 = 0, hl: UInt16 = 0, af_: UInt16 = 0, bc_: UInt16 = 0, de_: UInt16 = 0, hl_: UInt16 = 0, ix: UInt16 = 0, iy: UInt16 = 0, sp: UInt16 = 0, pc: UInt16 = 0) {
        z80.af = af
        z80.bc = bc
        z80.de = de
        z80.hl = hl
        z80.a_ = af_.highByte
        z80.f_ = af_.lowByte
        z80.b_ = bc_.highByte
        z80.c_ = bc_.lowByte
        z80.d_ = de_.highByte
        z80.e_ = de_.lowByte
        z80.h_ = hl_.highByte
        z80.l_ = hl_.lowByte
        z80.ix = ix
        z80.iy = iy
        z80.sp = sp
        z80.pc = pc
    }

    func checkRegisters(af: UInt16 = 0, bc: UInt16 = 0, de: UInt16 = 0, hl: UInt16 = 0, af_: UInt16 = 0, bc_: UInt16 = 0, de_: UInt16 = 0, hl_: UInt16 = 0, ix: UInt16 = 0, iy: UInt16 = 0, sp: UInt16 = 0, pc: UInt16 = 0) {
        XCTAssertEqual(z80.af.highByte, af.highByte,
                       "Register A: expected \(af.highByte.toHex), actual \(z80.af.highByte.toHex)")
        // While we attempt basic emulation of the undocumented bits 3 and 5,
        // we're not going to fail a test because of them (at least, right now).
        // So we OR both values with 0b000101000 (0x28) to mask out any difference.
        XCTAssertEqual(z80.af.lowByte | 0x28, af.lowByte | 0x28,
                       "Register F [SZ5H3PNC]: expected \(af.lowByte.toBinary), actual \(z80.af.lowByte.toBinary)")
        XCTAssertEqual(z80.bc, bc, "Register BC mismatch")
        XCTAssertEqual(z80.de, de, "Register DE mismatch")
        XCTAssertEqual(z80.hl, hl, "Register HL mismatch")
        XCTAssertEqual(z80.af_, af_, "Register AF\' mismatch")
        XCTAssertEqual(z80.bc_, bc_, "Register BC\' mismatch")
        XCTAssertEqual(z80.de_, de_, "Register DE\' mismatch")
        XCTAssertEqual(z80.hl_, hl_, "Register HL\' mismatch")
        XCTAssertEqual(z80.ix, ix, "Register IX mismatch")
        XCTAssertEqual(z80.iy, iy, "Register IY mismatch")
        XCTAssertEqual(z80.sp, sp, "Register SP mismatch")
        XCTAssertEqual(z80.pc, pc, "Register PC mismatch")
    }

    func checkSpecialRegisters(i: UInt8 = 0, r: UInt8 = 0, iff1: Bool = false, iff2: Bool = false, tStates: Int = 0) {
        XCTAssertEqual(z80.i, i, "Register I mismatch")
        XCTAssertEqual(z80.iff1, iff1, "Register IFF1 mismatch")
        XCTAssertEqual(z80.iff2, iff2, "Register IFF2 mismatch")
        XCTAssertEqual(z80.tStates, tStates, "tStates mismatch")
    }
    
    // Test instruction 01 | LD BC, **
    func test_01() {
      // Set up machine initial state
      loadRegisters(
          af: 0x0000,
          bc: 0x0000,
          de: 0x0000,
          hl: 0x0000,
          af_: 0x0000,
          bc_: 0x0000,
          de_: 0x0000,
          hl_: 0x0000,
          ix: 0x0000,
          iy: 0x0000,
          sp: 0x0000,
          pc: 0x0000);
      z80.i = 0x00;
      z80.r = 0x00;
      z80.iff1 = false;
      z80.iff2 = false;
      poke(0x0000, 0x01);
      poke(0x0001, 0x12);
      poke(0x0002, 0x34);

      // Execute machine for tState cycles
      while (z80.tStates < 1) {
        _ = z80.executeNextInstruction();
      }

      // Test machine state is as expected
      checkRegisters(
          af: 0x0000,
          bc: 0x3412,
          de: 0x0000,
          hl: 0x0000,
          af_: 0x0000,
          bc_: 0x0000,
          de_: 0x0000,
          hl_: 0x0000,
          ix: 0x0000,
          iy: 0x0000,
          sp: 0x0000,
          pc: 0x0003);
      checkSpecialRegisters(
          i: 0x00, r: 0x01, iff1: false, iff2: false, tStates: 10);
    }

