      z80.r = 0x00;
      z80.iff1 = false;
      z80.iff2 = false;
      poke(0x6d33, 0xff);

      // Execute machine for tState cycles
      while (z80.tStates < 1) {
        _ = z80.executeNextInstruction();
      }

      // Test machine state is as expected
      checkRegisters(
          af: 0x0000,
          bc: 0x0000,
          de: 0x0000,
          hl: 0x0000,
          af_: 0x0000,
          bc_: 0x0000,
          de_: 0x0000,
          hl_: 0x0000,
          ix: 0x0000,
          iy: 0x0000,
          sp: 0x5505,
          pc: 0x0038);
      checkSpecialRegisters(
          i: 0x00, r: 0x01, iff1: false, iff2: false, tStates: 11);
      XCTAssertEqual(peek(21765), 0x34);
      XCTAssertEqual(peek(21766), 0x6d);
    }

}
